module.exports = function(a) {
    require("matchdep").filterDev("grunt-*").forEach(a.loadNpmTasks), a.initConfig({
        pkg: a.file.readJSON("package.json"),
        uglify: {
            options: {},
            build: {
                src: "src/<%= pkg.name %>.js",
                dest: "build/<%= pkg.name %>.min.js"
            }
        },
        jade: {
            compile: {
                options: {
                    pretty: !0
                },
                files: [ {
                    cwd: "views",
                    src: "**/*.jade",
                    dest: "build",
                    expand: !0,
                    ext: ".html"
                } ]
            }
        },
        jshint: {
            files: [ "gruntfile.js" ]
        },
        connect: {
            server: {
                options: {
                    port: 1337
                }
            }
        },
        watch: {
            files: [ "<%= jshint.files %>" ],
            tasks: [ "jshint" ]
        }
    }), a.registerTask("serve", "connect:server"), a.registerTask("compile", "jade"), 
    a.registerTask("default", [ "jshint", "jade:compile", "connect" ]);
};